import { Component, EventEmitter, Input, Output, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, FormArray } from '@angular/forms';
import { InterviewService, SubjectiveQuestion, ChatRequest, ChatResponse } from '../../services/interview.service';

@Component({
  selector: 'app-subjective-qa',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './subjective-qa.component.html',
  styleUrls: ['./subjective-qa.component.css']
})
export class SubjectiveQAComponent implements OnInit {
  @Input() domain: string = '';
  @Input() resumeId: string = '';
  @Output() answersUpdate = new EventEmitter<any[]>();

  subjectiveForm: FormGroup;
  questions: SubjectiveQuestion[] = [];
  currentQuestionIndex = 0;
  isSubmitting = false;
  submittedAnswers: any[] = [];
  evaluationResults: { [questionId: string]: ChatResponse } = {};

  constructor(
    private fb: FormBuilder,
    private interviewService: InterviewService
  ) {
    this.subjectiveForm = this.fb.group({
      answers: this.fb.array([])
    });
  }

  ngOnInit() {
    this.loadSubjectiveQuestions();
  }

  loadSubjectiveQuestions() {
    this.interviewService.getSubjectiveQuestions(this.domain).subscribe({
      next: (questions) => {
        this.questions = questions;
        this.setupForm();
      },
      error: (error) => {
        console.error('Error loading subjective questions:', error);
        // Fallback to dummy questions if backend is not available
        this.loadDummyQuestions();
      }
    });
  }

  loadDummyQuestions() {
    this.questions = [
      {
        id: 'subj-1',
        title: 'Object-Oriented Programming Concepts',
        description: 'Explain the four fundamental principles of Object-Oriented Programming with examples.',
        domain: this.domain,
        difficulty: 'MEDIUM',
        questionType: 'SUBJECTIVE'
      },
      {
        id: 'subj-2',
        title: 'Database Optimization',
        description: 'Describe various database optimization techniques and when you would use each one.',
        domain: this.domain,
        difficulty: 'MEDIUM',
        questionType: 'SUBJECTIVE'
      },
      {
        id: 'subj-3',
        title: 'RESTful API Design',
        description: 'What are the key principles of RESTful API design? Provide examples of good and bad API design.',
        domain: this.domain,
        difficulty: 'MEDIUM',
        questionType: 'SUBJECTIVE'
      },
      {
        id: 'subj-4',
        title: 'Error Handling Best Practices',
        description: 'Discuss error handling best practices in software development. How do you handle exceptions in your preferred programming language?',
        domain: this.domain,
        difficulty: 'MEDIUM',
        questionType: 'SUBJECTIVE'
      }
    ];
    this.setupForm();
  }

  setupForm() {
    const answersArray = this.subjectiveForm.get('answers') as FormArray;
    answersArray.clear();
    
    this.questions.forEach(() => {
      answersArray.push(this.fb.control(''));
    });
  }

  get answersArray(): FormArray {
    return this.subjectiveForm.get('answers') as FormArray;
  }

  getCurrentQuestion(): SubjectiveQuestion | null {
    return this.questions[this.currentQuestionIndex] || null;
  }

  // Helper methods for template
  getCompletedQuestionsCount(): number {
    return Object.keys(this.evaluationResults).length;
  }

  getCorrectAnswersCount(): number {
    return Object.values(this.evaluationResults).filter(r => r.correct).length;
  }

  hasEvaluationResults(): boolean {
    return Object.keys(this.evaluationResults).length > 0;
  }

  navigateToQuestion(index: number) {
    if (index >= 0 && index < this.questions.length) {
      this.currentQuestionIndex = index;
    }
  }

  nextQuestion() {
    if (this.currentQuestionIndex < this.questions.length - 1) {
      this.currentQuestionIndex++;
    }
  }

  previousQuestion() {
    if (this.currentQuestionIndex > 0) {
      this.currentQuestionIndex--;
    }
  }

  canNavigateNext(): boolean {
    return this.currentQuestionIndex < this.questions.length - 1;
  }

  canNavigatePrevious(): boolean {
    return this.currentQuestionIndex > 0;
  }

  async submitAnswer() {
    const currentQuestion = this.getCurrentQuestion();
    const currentAnswer = this.answersArray.at(this.currentQuestionIndex).value;
    
    if (!currentQuestion || !currentAnswer.trim()) {
      return;
    }

    this.isSubmitting = true;

    try {
      const chatRequest: ChatRequest = {
        question: currentQuestion.description,
        resumeId: this.resumeId,
        domain: this.domain,
        expectedAnswer: currentAnswer, // The candidate's answer becomes the "expected answer" for comparison
        questionType: 'SUBJECTIVE'
      };

      // Send to backend for evaluation
      const response = await this.interviewService.sendChatResponse(chatRequest).toPromise();
      
      if (response) {
        this.evaluationResults[currentQuestion.id] = response;
        
        // Update submitted answers
        const answerData = {
          questionId: currentQuestion.id,
          question: currentQuestion.description,
          answer: currentAnswer,
          similarity: response.similarity,
          correct: response.correct,
          feedback: response.botResponse,
          timestamp: new Date()
        };
        
        // Update or add answer
        const existingIndex = this.submittedAnswers.findIndex(a => a.questionId === currentQuestion.id);
        if (existingIndex >= 0) {
          this.submittedAnswers[existingIndex] = answerData;
        } else {
          this.submittedAnswers.push(answerData);
        }
        
        this.answersUpdate.emit(this.submittedAnswers);
        
        // Move to next question if available
        if (this.canNavigateNext()) {
          this.nextQuestion();
        }
      }
    } catch (error) {
      console.error('Error submitting answer:', error);
      // Still save the answer locally even if evaluation fails
      const answerData = {
        questionId: currentQuestion.id,
        question: currentQuestion.description,
        answer: currentAnswer,
        timestamp: new Date(),
        error: 'Evaluation failed'
      };
      
      const existingIndex = this.submittedAnswers.findIndex(a => a.questionId === currentQuestion.id);
      if (existingIndex >= 0) {
        this.submittedAnswers[existingIndex] = answerData;
      } else {
        this.submittedAnswers.push(answerData);
      }
      
      this.answersUpdate.emit(this.submittedAnswers);
    } finally {
      this.isSubmitting = false;
    }
  }

  getAnswerStatus(index: number): string {
    const question = this.questions[index];
    const result = this.evaluationResults[question.id];
    
    if (!result) return 'unanswered';
    if (result.correct) return 'correct';
    if (result.similarity && result.similarity > 0.5) return 'partial';
    return 'incorrect';
  }

  getProgressPercentage(): number {
    const answeredCount = Object.keys(this.evaluationResults).length;
    return Math.round((answeredCount / this.questions.length) * 100);
  }

  isQuestionAnswered(index: number): boolean {
    const question = this.questions[index];
    return !!this.evaluationResults[question.id];
  }

  getSimilarityScore(questionId: string): number | null {
    const result = this.evaluationResults[questionId];
    return result?.similarity || null;
  }

  getAnswerFeedback(questionId: string): string | null {
    const result = this.evaluationResults[questionId];
    return result?.botResponse || null;
  }
}
