import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { InterviewService } from '../../services/interview.service';

@Component({
  selector: 'app-interview-landing',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './interview-landing.component.html',
  styleUrls: ['./interview-landing.component.css']
})
export class InterviewLandingComponent implements OnInit {
  candidateForm: FormGroup;
  interviewConfig: any = null;
  availableDomains: string[] = [];
  isLoading = false;
  agreed = false;

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private interviewService: InterviewService
  ) {
    this.candidateForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(2)]],
      email: ['', [Validators.required, Validators.email]],
      domain: ['', Validators.required],
      experience: [0, [Validators.required, Validators.min(0)]]
    });
  }

  ngOnInit() {
    this.loadAvailableDomains();
    this.candidateForm.get('experience')?.valueChanges.subscribe(experience => {
      if (experience !== null && experience >= 0) {
        this.loadInterviewConfiguration(experience);
      }
    });
  }

  loadAvailableDomains() {
    this.interviewService.getAvailableDomains().subscribe({
      next: (domains) => {
        this.availableDomains = domains;
      },
      error: (error) => {
        console.error('Error loading domains:', error);
        // Fallback domains
        this.availableDomains = ['Java', 'React', 'Angular', 'Python', 'Node.js'];
      }
    });
  }

  loadInterviewConfiguration(experience: number) {
    this.interviewService.getInterviewConfiguration(experience).subscribe({
      next: (config) => {
        this.interviewConfig = config;
      },
      error: (error) => {
        console.error('Error loading interview configuration:', error);
      }
    });
  }

  onDomainChange() {
    const domain = this.candidateForm.get('domain')?.value;
    const experience = this.candidateForm.get('experience')?.value;
    
    if (domain && experience !== null) {
      this.checkAvailability(domain, experience);
    }
  }

  checkAvailability(domain: string, experience: number) {
    this.interviewService.checkInterviewAvailability(domain, experience).subscribe({
      next: (availability) => {
        console.log('Interview availability:', availability);
      },
      error: (error) => {
        console.error('Error checking availability:', error);
      }
    });
  }

  toggleAgreement() {
    this.agreed = !this.agreed;
  }

  startInterview() {
    if (this.candidateForm.valid && this.agreed) {
      this.isLoading = true;
      
      // Store candidate information for the interview
      const candidateInfo = this.candidateForm.value;
      sessionStorage.setItem('candidateInfo', JSON.stringify(candidateInfo));
      
      // Navigate to interview test page
      setTimeout(() => {
        this.router.navigate(['/interview-test'], {
          queryParams: {
            domain: candidateInfo.domain,
            experience: candidateInfo.experience
          }
        });
      }, 1000);
    }
  }

  getExperienceLevel(years: number): string {
    if (years <= 1) return 'Fresher (0-1 years)';
    if (years <= 3) return 'Junior (1-3 years)';
    if (years <= 6) return 'Mid-level (3-6 years)';
    return 'Senior (6+ years)';
  }
}
