import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';

interface InterviewSummary {
  candidateName: string;
  domain: string;
  experienceLevel: string;
  codingQuestions: number;
  mcqQuestions: number;
  subjectiveQuestions: number;
  totalQuestions: number;
  interviewDuration: string;
}

@Component({
  selector: 'app-thank-you',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './thank-you.component.html',
  styleUrls: ['./thank-you.component.css']
})
export class ThankYouComponent implements OnInit {
  interviewSummary: InterviewSummary | null = null;
  isClosing: boolean = false;

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.loadInterviewSummary();
  }

  private loadInterviewSummary(): void {
    // Get summary data from session storage
    const summaryData = sessionStorage.getItem('interviewSummary');
    
    if (summaryData) {
      this.interviewSummary = JSON.parse(summaryData);
    } else {
      // Fallback if no summary data found
      this.interviewSummary = {
        candidateName: 'Candidate',
        domain: 'Unknown',
        experienceLevel: 'Unknown',
        codingQuestions: 0,
        mcqQuestions: 0,
        subjectiveQuestions: 0,
        totalQuestions: 0,
        interviewDuration: '0 minutes'
      };
    }

    // Clear session storage
    this.clearSessionData();
  }

  private clearSessionData(): void {
    // Clear all interview-related session data
    sessionStorage.removeItem('candidateInfo');
    sessionStorage.removeItem('interviewSummary');
    sessionStorage.removeItem('interviewStartTime');
  }

  closeWindow(): void {
    // Provide immediate feedback
    this.isClosing = true;
    
    // First try to close the window
    this.attemptWindowClose();
  }

  private attemptWindowClose(): void {
    try {
      // Method 1: Try standard window.close()
      window.close();
      
      // If the window is still open after a short delay, try alternative methods
      setTimeout(() => {
        if (!window.closed) {
          this.tryAlternativeCloseMethods();
        }
      }, 100);
    } catch (error) {
      console.log('Standard window.close() failed, trying alternatives');
      this.tryAlternativeCloseMethods();
    }
  }

  private tryAlternativeCloseMethods(): void {
    try {
      // Method 2: Try with opener reference
      if (window.opener) {
        window.opener.postMessage('closeWindow', '*');
        window.close();
        return;
      }

      // Method 3: Try parent window close
      if (window.parent && window.parent !== window) {
        window.parent.postMessage('closeWindow', '*');
        return;
      }

      // Method 4: For Chrome/Edge - try forcing close
      if (navigator.userAgent.includes('Chrome') || navigator.userAgent.includes('Edge')) {
        window.open('', '_self', '');
        window.close();
        return;
      }

      // Method 5: If all else fails, show instruction message and navigate to home
      this.showCloseInstruction();
      
    } catch (error) {
      console.log('All close methods failed, showing instructions');
      this.showCloseInstruction();
    }
  }

  private showCloseInstruction(): void {
    // Reset the closing state
    this.isClosing = false;
    
    // Show a brief message then redirect to home
    alert('Interview completed successfully! You can now close this window manually or click OK to return to the home page.');
    
    // Give user option to go to home page
    setTimeout(() => {
      this.router.navigate(['/']);
    }, 1000);
  }

  // Alternative method - go back to home page instead of closing
  goToHomePage(): void {
    this.router.navigate(['/']);
  }
}
