export class InterviewTestPageComponent {
  constructor(fb, interviewService, route, router) {
    this.fb = fb;
    this.interviewService = interviewService;
    this.route = route;
    this.router = router;
    this.activeSection = 'coding';
    this.candidateInfo = null;
    // Timer properties
    this.timeRemaining = 45 * 60; // 45 minutes in seconds
    // Question data
    this.codingQuestions = [];
    this.mcqQuestions = [];
    this.currentCodingIndex = 0;
    // Navigation
    this.codingAnswers = {};
    this.mcqAnswers = [];
    this.subjectiveAnswers = [];
    // Loading states
    this.isLoading = true;
    this.isSubmitting = false;
    // Cache parsed options to prevent repeated parsing
    this.optionsCache = new Map();
    // Current question tracking for single-question view
    this.currentMCQIndex = 0;
    this.currentSubjectiveIndex = 0;
    this.codingForm = this.fb.group({
      code: ['']
    });
    this.mcqForm = this.fb.group({
      answers: this.fb.array([])
    });
    this.resumeId = this.interviewService.generateResumeId();
  }
  ngOnInit() {
    this.loadCandidateInfo();
    this.loadQuestions();
    this.startTimer();
  }
  ngOnDestroy() {
    if (this.timerInterval) {
      clearInterval(this.timerInterval);
    }
  }
  loadCandidateInfo() {
    this.candidateInfo = this.interviewService.getCandidateInfo();
    if (!this.candidateInfo) {
      // Redirect to landing page if no candidate info
      this.router.navigate(['/']);
      return;
    }
  }
  loadQuestions() {
    if (!this.candidateInfo) return;
    const {
      domain,
      experience
    } = this.candidateInfo;
    // Load complete question set
    this.interviewService.getCompleteQuestionSet(domain, experience).subscribe({
      next: data => {
        this.codingQuestions = data.codingQuestions || [];
        this.mcqQuestions = data.mcqQuestions || [];
        this.setupForms();
        this.isLoading = false;
      },
      error: error => {
        console.error('Error loading questions:', error);
        this.loadFallbackQuestions();
        this.isLoading = false;
      }
    });
  }
  loadFallbackQuestions() {
    // Fallback questions if backend is not available
    this.codingQuestions = [{
      id: 'coding-1',
      title: 'Two Sum Problem',
      description: 'Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.',
      starterCode: 'function twoSum(nums, target) {\n    // Your code here\n}',
      domain: this.candidateInfo?.domain || 'General',
      difficulty: 'EASY',
      experienceLevel: 'FRESHER'
    }];
    this.mcqQuestions = [{
      id: 'mcq-1',
